	var (
		users     = make(map[int]user)
		usermutex = &sync.mutex{}
		nextid    = 1
	)

	// add some sample data
	users[1] = user{id: 1, name: "alice", age: 28}
	users[2] = user{id: 2, name: "bob", age: 32}
