	defer s.mu.unlock()

	now := time.now()
	book := book{
		id:          s.nextid,
		title:       req.title,
		author:      req.author,
		isbn:        req.isbn,
		pages:       req.pages,
		publishedat: req.publishedat,
		createdat:   now,
		updatedat:   now,
	}

	s.books[s.nextid] = book
