package main

import (
	"sync"
	"time"
)

// BookStore is an in-memory store for books
type BookStore struct {
	mu     sync.RWMutex
	books  map[int]Book
	nextID int
}

// NewBookStore creates a new BookStore
func NewBookStore() *BookStore {
	return &BookStore{
		books:  make(map[int]Book),
		nextID: 1,
	}
}

// GetBooks returns all books
func (s *BookStore) GetBooks() []Book {
	s.mu.RLock()
	defer s.mu.RUnlock()

	books := make([]Book, 0, len(s.books))
	for _, book := range s.books {
		books = append(books, book)
	}
	return books
}

// GetBook returns a book by ID
func (s *BookStore) GetBook(id int) (Book, bool) {
	s.mu.RLock()
	defer s.mu.RUnlock()

	book, exists := s.books[id]
	return book, exists
}

// AddBook adds a new book
func (s *BookStore) AddBook(req BookRequest) Book {
	s.mu.Lock()
	defer s.mu.Unlock()

	now := time.Now()
	book := Book{
		ID:          s.nextID,
		Title:       req.Title,
		Author:      req.Author,
		ISBN:        req.ISBN,
		Pages:       req.Pages,
		PublishedAt: req.PublishedAt,
		CreatedAt:   now,
		UpdatedAt:   now,
	}

	s.books[s.nextID] = book
	s.nextID++

	return book
}

// UpdateBook updates an existing book
func (s *BookStore) UpdateBook(id int, req BookRequest) (Book, bool) {
	s.mu.Lock()
	defer s.mu.Unlock()

	book, exists := s.books[id]
	if !exists {
		return Book{}, false
	}

	book.Title = req.Title
	book.Author = req.Author
	book.ISBN = req.ISBN
	book.Pages = req.Pages
	book.PublishedAt = req.PublishedAt
	book.UpdatedAt = time.Now()

	s.books[id] = book
	return book, true
}

// DeleteBook deletes a book by ID
func (s *BookStore) DeleteBook(id int) bool {
	s.mu.Lock()
	defer s.mu.Unlock()

	if _, exists := s.books[id]; !exists {
		return false
	}

	delete(s.books, id)
	return true
}

